

document.addEventListener('DOMContentLoaded', function() {


	farm_browser({

		filter : { 'mozilla':'22', 'chrome':'20', 'msie':'10','safari':'6','opera':'15' },

		close : function (data){ 

			console.log(data)

			browser_alert( data )

			if(data.passFilter){

					browser( data )

			}else{

					browser_alert( data )
			}

			return true; 

		}
	})

	function browser( data ){

		var tpl_browser = '';
		tpl_browser += '<section class="farm-bwa_bg">';	
		tpl_browser += '  <div class="farm-bwa">';	
		tpl_browser += '     <div class="vertical-align-wrap">';	
		tpl_browser += '        <div class="vertical-align vertical-align--middle">';

		tpl_browser += 'Browser: '+data.name+' | Version: '+ data.fullVersion+' | OS: '+ data.os

		tpl_browser += '       </div> ';	   
		tpl_browser += '    </div>';	
		tpl_browser += '  </div>';	
		tpl_browser += '<section>';


		document.getElementsByTagName("body")[0].innerHTML = tpl_browser;	
	}

	function browser_alert( data ){

		var tpl_browser_alert = '';
		tpl_browser_alert += '<section class="farm-bwa_bg">';		
		tpl_browser_alert += '  <div class="farm-bwa">';	
		tpl_browser_alert += '     <div class="vertical-align-wrap">';	
		tpl_browser_alert += '        <div class="vertical-align vertical-align--middle">';	
		tpl_browser_alert += '         	<h1 class="title">Your browser is out of date!</h1>';	
		tpl_browser_alert += '          	<p class="text">Update your browser to view this website correctly.</p>';	
		tpl_browser_alert += '          	<ul>';	
		tpl_browser_alert += '             	<li>';	
		tpl_browser_alert += '              		<a href="https://www.google.com/chrome/browser/" target="_blank">';	
		tpl_browser_alert += '               		<div class="grid-25 bwa-chrome">Chrome</div>';	
		tpl_browser_alert += '              		</a>';	
		tpl_browser_alert += '                </li>';	
		tpl_browser_alert += '                <li>';	
		tpl_browser_alert += '              		<a href="https://www.mozilla.org/firefox/new/" target="_blank">';	
		tpl_browser_alert += '                 		<div class="grid-25 bwa-firefox">Firefox</div>';	
		tpl_browser_alert += '              		</a>';	
		tpl_browser_alert += '                </li>';	
		tpl_browser_alert += '                <li>';	
		tpl_browser_alert += '              		<a href="http://windows.microsoft.com/es-es/internet-explorer/download-ie" target="_blank">';	
		tpl_browser_alert += '                 		<div class="grid-25 bwa-explorer">Explorer</div>';	
		tpl_browser_alert += '              		</a>';	
		tpl_browser_alert += '                </li>';	
		tpl_browser_alert += '                <li>';	
		tpl_browser_alert += '              		<a href="http://support.apple.com/kb/HT1338" target="_blank">';	
		tpl_browser_alert += '                 		<div class="grid-25 bwa-safari">Safari</div>';	
		tpl_browser_alert += '              		</a>';	
		tpl_browser_alert += '              	</li>';	
		tpl_browser_alert += '            </ul>';	
		tpl_browser_alert += '       </div> ';	   
		tpl_browser_alert += '    </div>';	
		tpl_browser_alert += '  </div>';	
		tpl_browser_alert += '<section>';

		document.getElementsByTagName("body")[0].innerHTML = tpl_browser_alert;	
	}


});





