    function animation_end(id_page, callback){
        var opts = that.options;

        var animEndEN = animEndEventName();

        if(opts.animation){
            //$("#"+id_page).on("animationend webkitAnimationEnd oAnimationEnd MSAnimationEnd",
            $("#"+id_page).on(animEndEN,
            function(e){
                callback();

                $(this).off(e);
            });
        }
        else{
            callback();
        }
    };



